mapper.select:
select id, parent_id, code, title, keywords, description, sort_order, deleted_status, created_user_id, created_by, created_on, created_ip, updated_user_id, updated_by, updated_on, updated_ip, version from spring_article_category where deleted_status=0
and title like concat(#{title,jdbcType=VARCHAR},'%')
and parent_id = #{parentId,jdbcType=VARCHAR}
