mapper.select:

    select id, code, title, menu_flag, vue_icon, vue_url, angular_icon, angular_url, 
    parent_id, parent_name, sort_code, enable_edit, enable_delete, deleted_status, show_status, 
    system_id, created_user_id, created_by, created_on, created_ip, updated_user_id, 
    updated_by, updated_on, updated_ip
    from spring_resource where deleted_status=0


				and code = #{code,jdbcType=VARCHAR}


				and k = #{k,jdbcType=VARCHAR}


				and parent_id = #{parent_id,jdbcType=VARCHAR}
