mapper.select:

    select id, from_user_id, from_user_name, to_user_id, to_user_name, title, content, 
    status, deleted_status, created_user_id, created_by, created_on, created_ip, updated_user_id, 
    updated_by, updated_on, updated_ip
    from spring_site_message  where deleted_status=0


				and to_user_id = #{toUserId,jdbcType=VARCHAR}


				and title = #{title,jdbcType=VARCHAR}
