mapper.select:

    select id, code, k, v, sort_code, enable_edit, enable_delete, deleted_status, created_user_id, 
    created_by, created_on, created_ip, updated_user_id, updated_by, updated_on, updated_ip
    from spring_parameter  where deleted_status=0


				and code = #{code,jdbcType=VARCHAR}


				and k = #{k,jdbcType=VARCHAR}


				and v = #{v,jdbcType=VARCHAR}
